package day4;

public class Switchcase3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String browser ="firefoxa";
		 switch(browser)
		 {
		 case "Chrome":
			 System.out.println("Start Chrome Session");
			 break;
		 case "Firefox":
			 System.out.println("Start Firefox Session");
			 break;
		 case "Safari":
			 System.out.println("Start Safari Session");
			 break;
		default:
			 System.out.println("Please use Chrome, Firefox or Safari");
			 
	}

	}

}
